import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Room } from 'src/app/@core/models/room.model';
import { MessageService } from 'src/app/@services/message.service';
import { RoomService } from 'src/app/@services/room.service';

@Component({
  selector: 'app-add-room',
  templateUrl: './add-room.component.html',
  styleUrls: ['./add-room.component.scss'],
})
export class AddRoomComponent implements OnInit {
  form!: FormGroup;
  imageRoom = '';
  fallback ='data:image/png;base64,';
  constructor(
    private fb: FormBuilder,
    private roomService: RoomService,
    private messageService: MessageService
  ) {
    this.initForm();
  }

  ngOnInit(): void {}

  initForm() {
    this.form = this.fb.group({
      room_name: ['', Validators.required],
      price: ['', Validators.required],
      electric_price: ['', Validators.required],
      water_price: ['', Validators.required],
      network_price: ['', Validators.required],
      max_number_people: ['', Validators.required],
      type: ['', Validators.required],
      description: [''],
      location: ['', Validators.required],
      images: [''],
    });
  }

  addRoom() {
    console.log('ok');
    if (this.form.invalid) {
      Object.values(this.form.controls).forEach((control) => {
        if (control.invalid) {
          control.markAsDirty();
          control.updateValueAndValidity({ onlySelf: true });
        }
      });
    } else {
      const room: Room = {
        id: '',
        room_name: this.form.value.room_name,
        price: this.form.value.price,
        electric_price: this.form.value.electric_price,
        water_price: this.form.value.water_price,
        network_price: this.form.value.network_price,
        max_number_people: this.form.value.max_number_people,
        type: this.form.value.type,
        location: this.form.value.location,
        description: this.form.value.description,
        isBooking: false,
        customer: '',
        store: '',
        images: this.form.value.images,
      };
      this.roomService.createRoom(room).subscribe({
        next: (data) => {
          this.messageService.showMessage({
            content: 'Bạn đã tạo phòng thành công',
          });
        },
        error: (error) => {
          this.messageService.showMessage({
            content: error
          })
        }
      });
    }
  }
}
